function showAlert(e,t,n,r,i,s,o,u){$("#alert_header").html(e);$("#alert_body").html(t);$("#alert_cancel").html(n);$("#alert_submit").html(r);if(u){$("#alert").modal({backdrop:"static",keyboard:!1});$("#close_alert_button").hide()}else{$("#alert").modal({backdrop:!0,keyboard:!0});$("#close_alert_button").show()}$("#alert").modal("show");$("#alert_cancel").unbind("click");$("#alert_cancel").click(function(){$("#alert").modal("hide");s()});$("#alert_submit").unbind("click");$("#alert_submit").click(function(){$("#alert").modal("hide");i()});o==1?$("#alert_cancel").hide():$("#alert_cancel").show()};