function checkSession(){$.ajax({type:"POST",url:"../scripts/check_session.php",data:{},success:function(e){e!=1&&showAlert("Session expired","Your session has expired.  Please logout and log back in.","","Ok",function(){window.location="../scripts/logout.php"},null,!0,!0)}})}$(document).ready(function(){checkSession();setInterval(function(){checkSession()},1e3)});